import MainPage from "./page/main-page/main-page";
import ThemeProvider from './theme/themeProvider';
import GlobalStyle from './theme/GlobalStyle';
import AppLayout from "./theme/appLayout";
import { useState } from "react";
import theme, { darkTheme, lightTheme } from "./theme/theme";

let isDarkMode = true;

const switchHandler = () => {
  isDarkMode = !isDarkMode;
  const themeDom = document.getElementById('themeProvider');
  themeDom.className = isDarkMode ? 'darkTheme' : 'lightTheme';
  themeDom.style.backgroundColor = 'black';
  console.log('다크모드?: ', isDarkMode);
};

function App() {
  const [darkFlag, setDarkState] = useState(true);
  return (
    <div id="themeProvider" className={darkFlag ? 'darkTheme' : 'lightTheme'}>
      <button onClick={setDarkState(!darkFlag)}>Switch</button>
      <MainPage/>
    </div>
  );
}

export default App;
