import { useState } from "react";
import Modal from "react-modal";
import { useTranslation } from "react-i18next";
import './preview-modal.css';

/*
 * userInfo: { id, image, name, custom_character }
*/
export default function PreviewModal({ title, srcs }) {
  const { t } = useTranslation();
  const modalStyle = {
    overlay: {
      backgroundColor: "#000000dd",
      zIndex: 1000
    },
    content: {
      backgroundColor: 'white',
      width: "fit-content",
      height: "fit-content",
      margin: "auto",
      border: "0",
      borderRadius: "10px",
      // padding: "20px 0",
      // overflow: 'hidden'
    }
  };

  return <Modal isOpen={true} style={modalStyle}>
    <div className="topBar">
      {t("preview.title")}{title ? ` - ${title}` : ''}
    </div>
    <div className="wrapper">
      <div className="top">
        <div className="leftBtn"></div>
        <div className="imageBox"></div>
        <div className="rightBtn"></div>
      </div>
      <div className="bottom">

      </div>
    </div>
  </Modal>;
}

function LeftIcon({onClick}) {
  return <svg onClick={onClick} fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
  <path clipRule="evenodd" fillRule="evenodd" d="M11.78 5.22a.75.75 0 0 1 0 1.06L8.06 10l3.72 3.72a.75.75 0 1 1-1.06 1.06l-4.25-4.25a.75.75 0 0 1 0-1.06l4.25-4.25a.75.75 0 0 1 1.06 0Z" />
</svg>;
};
function RightIcon({onClick}) {
  return <svg onClick={onClick} fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
  <path clipRule="evenodd" fillRule="evenodd" d="M8.22 5.22a.75.75 0 0 1 1.06 0l4.25 4.25a.75.75 0 0 1 0 1.06l-4.25 4.25a.75.75 0 0 1-1.06-1.06L11.94 10 8.22 6.28a.75.75 0 0 1 0-1.06Z" />
</svg>;
};
