import { useState } from "react";
import Modal from "react-modal";
import './preview-modal.css';

/*
 * userInfo: { id, image, name, custom_character }
*/
export default function PreviewModal({ srcs, onClose }) {
  const [selectedIdx, setSelectedIdx] = useState(0);
  const onClickWrapper = (e) => {
    e.stopPropagation();
  };

  const leftClick = (e) => {
    e.stopPropagation();
    setSelectedIdx(prev => prev + 1);
  };
  const rightClick = (e) => {
    e.stopPropagation();
    setSelectedIdx(prev => prev - 1);
  };

  return <div className="modal" onClick={onClose}>
    <div className="wrapper" onClick={onClickWrapper}>
      <div className="top">
        <div className={`arrowBtn${selectedIdx === 0 ? ' disabled' : ''}`} onClick={leftClick}><LeftIcon/></div>
        <div className="imageBox"><img src={srcs[selectedIdx]} alt="srcs"/></div>
        <div className={`arrowBtn${selectedIdx === (srcs.length - 1) ? ' disabled' : ''}`}><RightIcon/></div>
      </div>
      <div className="bottom">
        <div className="listView">
          {
            srcs.map((src, i) => {
              return <div className="imageWrapper"><img draggable={false} src={src} key={i} alt="srcs"/></div>;
            })
          }
        </div>
      </div>
    </div>
  </div>;
}

function LeftIcon({onClick}) {
  return <svg onClick={onClick} fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
  <path clipRule="evenodd" fillRule="evenodd" d="M11.78 5.22a.75.75 0 0 1 0 1.06L8.06 10l3.72 3.72a.75.75 0 1 1-1.06 1.06l-4.25-4.25a.75.75 0 0 1 0-1.06l4.25-4.25a.75.75 0 0 1 1.06 0Z" />
</svg>;
};
function RightIcon({onClick}) {
  return <svg onClick={onClick} fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
  <path clipRule="evenodd" fillRule="evenodd" d="M8.22 5.22a.75.75 0 0 1 1.06 0l4.25 4.25a.75.75 0 0 1 0 1.06l-4.25 4.25a.75.75 0 0 1-1.06-1.06L11.94 10 8.22 6.28a.75.75 0 0 1 0-1.06Z" />
</svg>;
};
